package frame;import IterationThree.IntermediateCode.tree.exp.*;public class InFrame extends frame.Access {    int offset;    public InFrame(int o) {	offset = o;    }    public Exp exp(Exp fp) {        return new MEM	    (new BINOP(BINOP.PLUS, fp, new CONST(offset)));    }    public String toString() {        Integer offset = new Integer(this.offset);	return offset.toString();    }}